var AsteroidEditorExtension = require('../');

describe('AsteroidEditorExtension', function(){
  var asteroidEditorExtension;
  
  beforeEach(function(){
    asteroidEditorExtension = new AsteroidEditorExtension;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      asteroidEditorExtension.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        asteroidEditorExtension.myMethod();
        done();
      }, 0);
    });
  });
});