/**
 * Expose `AsteroidEditorUtility`.
 */

module.exports = AsteroidEditorUtility;

/**
 * Module dependencies.
 */
 
var EventEmitter = require('events').EventEmitter
  , debug = require('debug')('asteroid-editor-utility')
  , AsteroidEditorExtension = require('asteroid-editor-extension')
  , util = require('util')
  , inherits = util.inherits
  , assert = require('assert');
  
/**
 * Create a new `AsteroidEditorUtility` with the given `options`.
 *
 * @param {Object} options
 * @return {AsteroidEditorUtility}
 */

function AsteroidEditorUtility(options) {
  options.layout = options.layout || '/layouts/editor-layout.ejs';
  AsteroidEditorExtension.apply(this, arguments);
  
  this.options = options || {};
  
  // throw an error if args are not supplied
  // assert(typeof options === 'object', 'AsteroidEditorUtility requires an options object');
  
  debug('created with options', options);
}

/**
 * Inherit from `EventEmitter`.
 */

inherits(AsteroidEditorUtility, AsteroidEditorExtension);

/**
 * Simplified APIs
 */

AsteroidEditorUtility.create =
AsteroidEditorUtility.createAsteroidEditorUtility = function () {
  // add simplified construction / sugar here
  return new AsteroidEditorUtility();
}

/**
 * Methods.
 */
 
AsteroidEditorUtility.prototype.myMethod = function () {
  throw new Error('AsteroidEditorUtility.myMethod not implemented');
}