var LoopbackEditorExtension = require('../');

describe('LoopbackEditorExtension', function(){
  var loopbackEditorExtension;
  
  beforeEach(function(){
    loopbackEditorExtension = new LoopbackEditorExtension;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      loopbackEditorExtension.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        loopbackEditorExtension.myMethod();
        done();
      }, 0);
    });
  });
});