var LoopbackEditorUtility = require('../');

describe('LoopbackEditorUtility', function(){
  var loopbackEditorUtility;
  
  beforeEach(function(){
    loopbackEditorUtility = new LoopbackEditorUtility;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      loopbackEditorUtility.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        loopbackEditorUtility.myMethod();
        done();
      }, 0);
    });
  });
});