var LoopbackSubEditor = require('../');

describe('LoopbackSubEditor', function(){
  var loopbackSubEditor;
  
  beforeEach(function(){
    loopbackSubEditor = new LoopbackSubEditor;
  });
  
  describe('.myMethod', function(){
    // example sync test
    it('should <description of behavior>', function() {
      loopbackSubEditor.myMethod();
    });
    
    // example async test
    it('should <description of behavior>', function(done) {
      setTimeout(function () {
        loopbackSubEditor.myMethod();
        done();
      }, 0);
    });
  });
});